import Foundationimport Combinestruct PokemonService {    private let baseURL = "https://pokeapi.co/api/v2/"        func fetchPokemons(offset: Int, limit: Int) -> AnyPublisher<[Pokemon], Error> {        let url = URL(string: "\(baseURL)pokemon?offset=\(offset)&limit=\(limit)")!                return URLSession.shared.dataTaskPublisher(for: url)            .map(\.data)            .decode(type: PokemonListResponse.self, decoder: JSONDecoder())            .map(\.results)            .eraseToAnyPublisher()    }        func fetchPokemonDetails(id: Int) -> AnyPublisher<PokemonDetails, Error> {        let url = URL(string: "\(baseURL)pokemon/\(id)")!                return URLSession.shared.dataTaskPublisher(for: url)            .map(\.data)            .decode(type: PokemonDetails.self, decoder: JSONDecoder())            .eraseToAnyPublisher()    }}