import Foundationimport SwiftData@Modelfinal class FavoritePokemon {    var pokemonId: Int    var pokemonName: String    var userId: String  // This will store the user's email    var dateAdded: Date        init(pokemonId: Int, pokemonName: String, userId: String, dateAdded: Date) {        self.pokemonId = pokemonId        self.pokemonName = pokemonName        self.userId = userId        self.dateAdded = dateAdded    }}