import Foundationimport SwiftData@Modelfinal class FavoritePokemon {    var userId: String    var pokemonId: Int    var pokemonName: String    var dateAdded: Date    var imageUrl: String?        init(userId: String, pokemonId: Int, pokemonName: String, imageUrl: String?) {        self.userId = userId        self.pokemonId = pokemonId        self.pokemonName = pokemonName        self.dateAdded = Date()        self.imageUrl = imageUrl    }}