import Foundationimport SwiftData@Modelfinal class FavoritePokemon {    @Attribute(.unique) var pokemonId: Int    var pokemonName: String    var userId: String    var dateAdded: Date    init(pokemonId: Int, pokemonName: String, userId: String, dateAdded: Date) {        self.pokemonId = pokemonId        self.pokemonName = pokemonName        self.userId = userId        self.dateAdded = dateAdded    }}