import Foundationimport SwiftUIstruct PokemonDetails: Codable {    let id: Int    let height: Int    let weight: Int    let stats: [PokemonStat]    let types: [String]        var officialArtwork: URL {        URL(string: "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/\(id).png")!    }}struct PokemonStat: Codable, Identifiable {    let id = UUID()    let name: String    let value: Int        enum CodingKeys: String, CodingKey {        case name, value    }}