import Foundationimport SwiftUI// PokemonDetails.swiftstruct PokemonDetails: Codable {    let id: Int    let name: String     let height: Int  // em decímetros    let weight: Int  // em hectogramas    let stats: [PokemonStat]    let types: [String]    let imageUrl: String? }struct PokemonStat: Codable, Identifiable {    let id = UUID()    let name: String    let value: Int  // Valor numérico da estatística    }