import Foundationstruct RawPokemonDetails: Codable {    let id: Int    let name: String    let height: Int    let weight: Int    let stats: [Stat]    let types: [TypeEntry]    let sprites: Sprites    struct Stat: Codable {        let baseStat: Int        let stat: StatDetail        struct StatDetail: Codable {            let name: String        }    }    struct TypeEntry: Codable {        let type: TypeDetail        struct TypeDetail: Codable {            let name: String        }    }    struct Sprites: Codable {        let frontDefault: String?    }}