struct LoginView: View {    @EnvironmentObject private var appState: AppState    @StateObject private var viewModel: LoginViewModel    @State private var mostrarRegistro = false        // Inicializador que injeta o AppState    init(appState: AppState) {        _viewModel = StateObject(wrappedValue: LoginViewModel(appState: appState))    }        var body: some View {        NavigationView {            VStack(spacing: DesignSystem.Spacing.m) {                // ... restante do código igual ao anterior ...                                Button("Entrar") {                    viewModel.login()                }                .padding()                .frame(maxWidth: .infinity)                .background(DesignSystem.Colors.primary)                .foregroundColor(.white)                .cornerRadius(DesignSystem.CornerRadius.medium)                .padding(.horizontal, DesignSystem.Spacing.m)                                // ... restante do código ...                                if viewModel.showError {                    Text(viewModel.errorMessage)                        .foregroundColor(DesignSystem.Colors.error)                        .padding(.top, DesignSystem.Spacing.s)                }            }            .padding(DesignSystem.Spacing.l)        }    }}