import SwiftUIimport SwiftData@mainstruct PokeExplorerApp: App {    private let container: ModelContainer        init() {        do {            container = try ModelContainer(for: User.self, FavoritePokemon.self)        } catch {            fatalError("Não foi possível criar o ModelContainer: \(error)")        }    }        var body: some Scene {        WindowGroup {            // Agora ele apenas chama a AppCoordinatorView, que faz todo o trabalho.            AppCoordinatorView()        }        .modelContainer(container)    }}