import Foundationstruct APIPokemonListResponse: Codable {    let results: [APIPokemonListItem]}struct APIPokemonListItem: Codable, Hashable, Identifiable {    let name: String    let url: String    var id: String { name }}struct APIPokemonDetail: Codable {    let id: Int    let name: String    let height: Int    let weight: Int    let sprites: Sprites    let types: [TypeElement]    struct Sprites: Codable {        let frontDefault: String    }    struct TypeElement: Codable {        let type: Species    }    struct Species: Codable {        let name: String    }}