// Core/Persistence/PersistenceModels.swiftimport Foundationimport SwiftData// A macro @Model transforma esta classe em uma tabela no banco de dados.@Modelfinal class User {    @Attribute(.unique) var email: String    var username: String    var passwordHash: String    @Relationship(deleteRule: .cascade, inverse: \FavoritePokemon.user)    var favoritePokemons: [FavoritePokemon] = []    init(email: String, username: String, passwordHash: String) {        self.email = email        self.username = username        self.passwordHash = passwordHash    }}// Esta tamb√©m vira uma tabela, com um relacionamento com a tabela User.@Modelfinal class FavoritePokemon {    @Attribute(.unique) var pokemonID: Int    var name: String    var imageURL: String    var user: User?    init(pokemonID: Int, name: String, imageURL: String) {        self.pokemonID = pokemonID        self.name = name        self.imageURL = imageURL    }}