import SwiftUIenum DesignTokens {    enum Colors {        // Cor direta do SwiftUI        static let primary: Color = .red        // Cores de sistema (UIColor) convertidas para Color do SwiftUI        static let background: Color = Color(.systemGray6)        static let textPrimary: Color = Color(.label)        static let textSecondary: Color = Color(.secondaryLabel)    }    enum Fonts {        static func title() -> Font { .system(.largeTitle, design: .rounded).bold() }        static func body() -> Font { .system(.body, design: .rounded) }        static func caption() -> Font { .system(.caption, design: .rounded).bold() }    }    enum Spacing {        static let small: CGFloat = 8        static let medium: CGFloat = 16        static let large: CGFloat = 24    }    enum CornerRadius {        static let standard: CGFloat = 12    }}