import SwiftUIstruct ProfileView: View {    @ObservedObject var coordinator: AppCoordinator    var body: some View {        VStack(spacing: DesignTokens.Spacing.large) {            Image(systemName: "person.crop.circle.fill")                .font(.system(size: 100))                .foregroundColor(DesignTokens.Colors.textSecondary)                        VStack {                if let user = coordinator.currentUser {                    Text(user.username)                        .font(DesignTokens.Fonts.title())                        .foregroundColor(DesignTokens.Colors.textPrimary)                                        Text(user.email)                        .font(.headline)                        .foregroundColor(DesignTokens.Colors.textSecondary)                }            }            Spacer()            Button("Sair", role: .destructive, action: coordinator.logout)                .buttonStyle(.bordered)                .tint(.red)                        Spacer().frame(height: 20)        }        .padding()    }}