import SwiftUIstruct LoginView: View {    @StateObject var viewModel: AuthViewModel        var body: some View {        VStack(spacing: DesignTokens.Spacing.medium) {            Text("Pok√©Explorer")                .font(DesignTokens.Fonts.title())                .foregroundColor(DesignTokens.Colors.primary)            Picker("Mode", selection: $viewModel.authMode) {                Text("Login").tag(AuthViewModel.AuthMode.login)                Text("Cadastro").tag(AuthViewModel.AuthMode.register)            }            .pickerStyle(.segmented)            if viewModel.authMode == .register {                TextField("Nome", text: $viewModel.username)                    .textContentType(.username)            }                        TextField("Email", text: $viewModel.email)                .keyboardType(.emailAddress)                .autocapitalization(.none)            SecureField("Senha", text: $viewModel.password)                .textContentType(.password)            if let errorMessage = viewModel.errorMessage {                Text(errorMessage)                    .foregroundColor(.red)                    .font(.caption)            }            Button(action: viewModel.submit) {                Text(viewModel.authMode == .login ? "Entrar" : "Criar Conta")                    .frame(maxWidth: .infinity)            }            .buttonStyle(.borderedProminent)            .tint(DesignTokens.Colors.primary)            .disabled(viewModel.isLoading)        }        .textFieldStyle(.roundedBorder)        .padding()    }}