import SwiftUIstruct RegisterView: View {    @StateObject private var viewModel = RegisterViewModel()    @Environment(\.presentationMode) var presentationMode        var body: some View {        NavigationView {            VStack(spacing: 20) {                // Header                VStack {                    Image(systemName: "person.crop.circle.badge.plus")                        .resizable()                        .scaledToFit()                        .frame(width: 80, height: 80)                        .foregroundColor(.blue)                                        Text("Criar Nova Conta")                        .font(.title)                        .fontWeight(.bold)                }                .padding(.top, 20)                                // Campos de formulário                VStack(spacing: 15) {                    TextField("Nome Completo", text: $viewModel.nome)                        .padding()                        .background(Color(.systemGray6))                        .cornerRadius(10)                        .autocapitalization(.words)                                        TextField("Email", text: $viewModel.email)                        .padding()                        .background(Color(.systemGray6))                        .cornerRadius(10)                        .keyboardType(.emailAddress)                        .autocapitalization(.none)                                        SecureField("Senha", text: $viewModel.senha)                        .padding()                        .background(Color(.systemGray6))                        .cornerRadius(10)                                        SecureField("Confirmar Senha", text: $viewModel.confirmarSenha)                        .padding()                        .background(Color(.systemGray6))                        .cornerRadius(10)                }                .padding(.horizontal)                                // Botão de registro                Button(action: {                    if viewModel.registrar() {                        presentationMode.wrappedValue.dismiss()                    }                }) {                    Text("Registrar")                        .fontWeight(.bold)                        .foregroundColor(.white)                        .frame(maxWidth: .infinity)                        .padding()                        .background(Color.blue)                        .cornerRadius(10)                }                .padding(.horizontal)                .padding(.top, 10)                                // Botão de voltar                Button(action: {                    presentationMode.wrappedValue.dismiss()                }) {                    Text("Voltar para Login")                        .foregroundColor(.blue)                        .padding(.top, 10)                }                                // Mensagem de erro                if viewModel.mostrarErro {                    Text(viewModel.mensagemErro)                        .foregroundColor(.red)                        .padding(.top)                }                                Spacer()            }            .padding()            .navigationBarTitle("Registro", displayMode: .inline)            .navigationBarItems(leading: Button(action: {                presentationMode.wrappedValue.dismiss()            }) {                Image(systemName: "chevron.left")                    .foregroundColor(.blue)            })        }    }}