import SwiftUIstruct LoginView: View {    @StateObject private var viewModel = LoginViewModel()    @State private var mostrarRegistro = false // Controla a navegação        var body: some View {        NavigationView {            VStack(spacing: 20) {                Text("Login")                    .font(.largeTitle)                    .bold()                                TextField("Email", text: $viewModel.email)                    .textFieldStyle(RoundedBorderTextFieldStyle())                    .autocapitalization(.none)                    .keyboardType(.emailAddress)                    .padding(.horizontal)                                SecureField("Senha", text: $viewModel.senha)                    .textFieldStyle(RoundedBorderTextFieldStyle())                    .padding(.horizontal)                                Button("Entrar") {                    if viewModel.login() {                        print("Login bem-sucedido!")                    }                 }                .padding()                .frame(maxWidth: .infinity)                .background(Color.blue)                .foregroundColor(.white)                .cornerRadius(10)                .padding(.horizontal)                                // Botão para navegar para o registro                Button("Criar nova conta") {                    mostrarRegistro = true                }                .foregroundColor(.blue)                .padding(.top)                                if viewModel.mostrarErro {                    Text(viewModel.mensagemErro)                        .foregroundColor(.red)                        .padding(.top)                }            }            .padding()            .navigationTitle("Bem-vindo")            .navigationBarHidden(true)            .sheet(isPresented: $mostrarRegistro) {                // Mostra a tela de registro como modal                RegisterView()            }        }    }}