import SwiftUIstruct LoginView: View {    @EnvironmentObject var authManager: AuthManager    @StateObject private var viewModel = LoginViewModel() // Usando StateObject corretamente    @State private var mostrarRegistro = false        init() {        // Inicializa o ViewModel uma Ãºnica vez        _viewModel = StateObject(wrappedValue: LoginViewModel())    }        var body: some View {        NavigationView {            VStack(spacing: 20) {                Text("Login")                    .font(.largeTitle)                    .bold()                                TextField("Email", text: $viewModel.email)                    .textFieldStyle(.roundedBorder)                    .autocapitalization(.none)                    .keyboardType(.emailAddress)                    .padding(.horizontal)                                SecureField("Senha", text: $viewModel.senha)                    .textFieldStyle(.roundedBorder)                    .padding(.horizontal)                                Button(action: {                    viewModel.login(authManager: authManager)                }) {                    if viewModel.isLoading {                        ProgressView()                            .progressViewStyle(CircularProgressViewStyle(tint: .white))                    } else {                        Text("Entrar")                    }                }                .padding()                .frame(maxWidth: .infinity)                .background(Color.blue)                .foregroundColor(.white)                .cornerRadius(10)                .padding(.horizontal)                .disabled(viewModel.isLoading)                                Button("Criar nova conta") {                    mostrarRegistro = true                }                .foregroundColor(.blue)                .padding(.top)                                if viewModel.mostrarErro {                    Text(viewModel.mensagemErro)                        .foregroundColor(.red)                        .padding(.top)                }            }            .padding()            .navigationTitle("Bem-vindo")            .navigationBarHidden(true)            .sheet(isPresented: $mostrarRegistro) {                RegisterView()            }        }    }}