import Foundationclass LoginViewModel: ObservableObject {    @Published var email = ""    @Published var senha = ""    @Published var mostrarErro = false    @Published var mensagemErro = ""        func login() -> Bool {        guard !email.isEmpty, !senha.isEmpty else {            mensagemErro = "Preencha todos os campos"            mostrarErro = true            return false        }        // Recupera dados salvos        if let userData = UserDefaults.standard.dictionary(forKey: "usuario") as? [String: String],           userData["email"] == email,           userData["senha"] == senha {            print("Login bem-sucedido")            return true        } else {            mensagemErro = "Email ou senha incorretos"            mostrarErro = true            return false        }    }    }