import Foundationclass LoginViewModel: ObservableObject {    @Published var email = ""    @Published var senha = ""    @Published var isLoading = false    @Published var mostrarErro = false    @Published var mensagemErro = ""        func login(authManager: AuthManager) {        isLoading = true        mostrarErro = false                DispatchQueue.main.asyncAfter(deadline: .now() + 1) {            self.isLoading = false                        if self.email.contains("@") && self.senha.count >= 6 {                authManager.login()            } else {                self.mensagemErro = "Email ou senha inv√°lidos"                self.mostrarErro = true            }        }    }}