import SwiftUIstruct PokemonDetailView: View {    @StateObject var viewModel: PokemonDetailViewModel        init(pokemon: Pokemon) {        _viewModel = StateObject(wrappedValue: PokemonDetailViewModel(pokemon: pokemon))    }        var body: some View {        ScrollView {            VStack(spacing: 20) {                // Header Section                if let url = viewModel.pokemonImageURL {                    AsyncImage(url: url) { phase in                        switch phase {                        case .success(let image):                            image                                .resizable()                                .scaledToFit()                                .frame(width: 200, height: 200)                        case .failure:                            Image(systemName: "questionmark.circle")                                .font(.largeTitle)                        default:                            ProgressView()                        }                    }                }                                Text(viewModel.pokemonName)                    .font(.largeTitle)                                if let id = viewModel.pokemon.pokemonId {                    Text("#\(String(format: "%03d", id))")                        .font(.title2)                        .foregroundColor(.gray)                }                                // Types Section                VStack(alignment: .leading) {                    Text("Types")                        .font(.headline)                    Text(viewModel.formattedTypes)                }                .frame(maxWidth: .infinity, alignment: .leading)                .padding()                .background(Color(.systemGray6))                .cornerRadius(10)                                // Stats Section                VStack(alignment: .leading) {                    Text("Stats")                        .font(.headline)                                        ForEach(viewModel.formattedStats) { stat in                        HStack {                            Text(stat.name.capitalized)                                .frame(width: 100, alignment: .leading)                                                        ProgressView(value: Double(stat.value), total: 150)                                                        Text("\(stat.value)")                                .frame(width: 40)                        }                    }                }                .padding()                .background(Color(.systemGray6))                .cornerRadius(10)                                // Physical Attributes                if let details = viewModel.pokemonDetails {                    HStack {                        VStack {                            Text("Height")                                .font(.headline)                            Text("\(details.height) dm")                        }                        .frame(maxWidth: .infinity)                                                VStack {                            Text("Weight")                                .font(.headline)                            Text("\(details.weight) hg")                        }                        .frame(maxWidth: .infinity)                    }                    .padding()                    .background(Color(.systemGray6))                    .cornerRadius(10)                }            }            .padding()        }        .navigationTitle(viewModel.pokemonName)        .navigationBarTitleDisplayMode(.inline)        .overlay {            if viewModel.isLoading {                ProgressView()            }        }        .alert("Error", isPresented: .constant(viewModel.error != nil)) {            Button("OK", role: .cancel) { viewModel.error = nil }        } message: {            Text(viewModel.error ?? "Unknown error")        }    }}