import Foundationclass RegisterViewModel: ObservableObject {    @Published var nome = ""    @Published var email = ""    @Published var senha = ""    @Published var confirmarSenha = ""    @Published var mostrarErro = false    @Published var mensagemErro = ""        func registrar() -> Bool {        // Limpa erros anteriores        mostrarErro = false                // Validações        guard !nome.isEmpty else {            mensagemErro = "Nome é obrigatório"            mostrarErro = true            return false        }                guard !email.isEmpty else {            mensagemErro = "Email é obrigatório"            mostrarErro = true            return false        }                guard senha == confirmarSenha else {            mensagemErro = "As senhas não coincidem"            mostrarErro = true            return false        }                guard senha.count >= 6 else {            mensagemErro = "A senha deve ter pelo menos 6 caracteres"            mostrarErro = true            return false        }                // Salva no UserDefaults (exemplo simples de persistência local)        let userData = ["nome": nome, "email": email, "senha": senha]        UserDefaults.standard.set(userData, forKey: "usuario")        print("Usuário registrado com sucesso")        return true            }}